CREATE TABLE IF NOT EXISTS "django_migrations" ("id" SERIAL PRIMARY KEY, "app" varchar(255) NOT NULL, "name" varchar(255) NOT NULL, "applied" timestamp NOT NULL);
CREATE TABLE IF NOT EXISTS "customer" ("id" SERIAL PRIMARY KEY, "password" varchar(128) NOT NULL, "last_login" timestamp NULL, "is_superuser" bool NOT NULL, "name" varchar(100) NOT NULL, "email" varchar(254) NOT NULL UNIQUE, "phone" varchar(10) NULL, "date_joined" timestamp NOT NULL, "is_staff" bool NOT NULL, "is_active" bool NOT NULL);
CREATE TABLE IF NOT EXISTS "extra_category" ("id" SERIAL PRIMARY KEY, "name" varchar(30) NOT NULL);
CREATE TABLE IF NOT EXISTS "menu_category" ("id" SERIAL PRIMARY KEY, "name" varchar(150) NOT NULL UNIQUE, "menu_slug" varchar(50) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS "django_admin_log" ("id" SERIAL PRIMARY KEY, "object_id" text NULL, "object_repr" varchar(200) NOT NULL, "action_flag" smallint unsigned NOT NULL CHECK ("action_flag" >= 0), "change_message" text NOT NULL, "content_type_id" integer NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "user_id" bigint NOT NULL REFERENCES "customer" ("id") DEFERRABLE INITIALLY DEFERRED, "action_time" timestamp NOT NULL);
CREATE TABLE IF NOT EXISTS "django_content_type" ("id" SERIAL PRIMARY KEY, "app_label" varchar(100) NOT NULL, "model" varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS "auth_group_permissions" ("id" SERIAL PRIMARY KEY, "group_id" integer NOT NULL REFERENCES "auth_group" ("id") DEFERRABLE INITIALLY DEFERRED, "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "auth_permission" ("id" SERIAL PRIMARY KEY, "content_type_id" integer NOT NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "codename" varchar(100) NOT NULL, "name" varchar(255) NOT NULL);
CREATE TABLE IF NOT EXISTS "auth_group" ("id" SERIAL PRIMARY KEY, "name" varchar(150) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS "province" ("id" SERIAL PRIMARY KEY, "province" varchar(100) NOT NULL, "province_slug" varchar(50) NOT NULL UNIQUE);
