CREATE TABLE IF NOT EXISTS "menu" ("id" SERIAL PRIMARY KEY, "name" varchar(150) NOT NULL, "price" decimal NOT NULL, "description" text NOT NULL, "quantity" integer unsigned NOT NULL CHECK ("quantity" >= 0), "product_image" varchar(100) NOT NULL, "is_active" bool NOT NULL, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL, "category_id" bigint NOT NULL REFERENCES "menu_category" ("id") DEFERRABLE INITIALLY DEFERRED, "restaurant_id" bigint NOT NULL REFERENCES "restaurant" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "extra_category_extras" ("id" SERIAL PRIMARY KEY, "extracategory_id" bigint NOT NULL REFERENCES "extra_category" ("id") DEFERRABLE INITIALLY DEFERRED, "extras_id" bigint NOT NULL REFERENCES "extras" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "cart_extras" ("id" SERIAL PRIMARY KEY, "cart_id" bigint NOT NULL REFERENCES "cart" ("id") DEFERRABLE INITIALLY DEFERRED, "extras_id" bigint NOT NULL REFERENCES "extras" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "cart" ("id" SERIAL PRIMARY KEY, "quantity" integer unsigned NOT NULL CHECK ("quantity" >= 0), "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL, "customer_id" bigint NOT NULL REFERENCES "customer" ("id") DEFERRABLE INITIALLY DEFERRED, "menu_id" bigint NOT NULL REFERENCES "menu" ("id") DEFERRABLE INITIALLY DEFERRED, "portion_id" bigint NULL REFERENCES "portions" ("id") DEFERRABLE INITIALLY DEFERRED);
