CREATE TABLE IF NOT EXISTS "customer_address" ("id" SERIAL PRIMARY KEY, "customer_id" bigint NOT NULL REFERENCES "customer" ("id") DEFERRABLE INITIALLY DEFERRED, "adress_id" bigint NOT NULL REFERENCES "adress" ("id") DEFERRABLE INITIALLY DEFERRED);
